$(document).ready(function(){$(".play").click(function(){var t=$("#game_token").val();$.ajax({type:"GET",url:"/updates",data:{token:t,button:"play"}}).done(function(t){if("success"==t.status){if(t.win===!0){var e=$("#credits").data("user-credits");$(".outcome").html("You win 1 credit for a total of "+t.total_credits+" credit(s) so far! Click again for another chance!"),$("#credit_count").html(e+t.total_credits)}else $(".outcome").html("Sorry you lost! So far you have earned "+t.total_credits+" in this game session! Click again for another chance!");"none"!=t.partner_image&&$(".ad-space").html("<img style='max-height:100px' src='"+t.partner_image+"' />")}else"closed"==t.status&&$(".outcome").html("Sorry this game has closed!")})})});